package com.rahul.mvvmjavaexample.viewmodels;

import android.os.AsyncTask;

import androidx.lifecycle.LiveData;
import androidx.lifecycle.MutableLiveData;
import androidx.lifecycle.ViewModel;

import com.rahul.mvvmjavaexample.models.NicePlace;
import com.rahul.mvvmjavaexample.repositories.NicePlaceRepository;

import java.util.List;

public class MainActivityViewModel extends ViewModel {

    private MutableLiveData<List<NicePlace>> mNicePlaces;
    private NicePlaceRepository mRepo;

    private MutableLiveData<Boolean> mIsUpdating = new MutableLiveData<>();

    public LiveData<List<NicePlace>> getNicePlaces(){
     return  mNicePlaces;
    }

    public void addNewValue(final NicePlace nicePlace){

        mIsUpdating.setValue(true);
        //To Use a server request mimiking
        new AsyncTask<Void,Void,Void>(){
            @Override
            protected Void doInBackground(Void... voids) {
                try {
                    Thread.sleep(2000);
                }catch (Exception e){
                    e.printStackTrace();
                }
                return null;
            }

            @Override
            protected void onPostExecute(Void aVoid) {
                super.onPostExecute(aVoid);
                List<NicePlace>  currentPlaces = mNicePlaces.getValue();
                currentPlaces.add(new NicePlace("Havana","data:image/jpeg;base64,"));
                mNicePlaces.postValue(currentPlaces);
                mIsUpdating.postValue(false);
            }
        }.execute();


    }

    public LiveData<Boolean> getIsUpdating(){
        return mIsUpdating;
    }

    public void init(){
        if(mNicePlaces!=null){
            return;
        }
        mRepo = NicePlaceRepository.getInstance();
        mNicePlaces = mRepo.getNicePlaces();

    }

}
